tables.table_schema:
CREATE TABLE Parking_Regulations (
  id INT PRIMARY KEY,
  regulation VARCHAR(255),
  fine_amount DECIMAL(10, 2)
);

CREATE TABLE Parking_Permits (
  permit_id INT PRIMARY KEY,
  vehicle_number VARCHAR(20),
  start_date DATE,
  end_date DATE
);

CREATE TABLE parking_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT
);

CREATE TABLE parking_permits (
    permit_id INT PRIMARY KEY,
    permit_type VARCHAR(100),
    permit_duration INT,
    permit_cost DECIMAL(10,2)
);

CREATE TABLE parking_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    location_address TEXT,
    location_capacity INT
);
