    destination VARCHAR(255) 
);

CREATE TABLE TrainSchedule (
    train_id INT PRIMARY KEY,
    train_name VARCHAR(255),
    departure_time TIME,
    arrival_time TIME
);

CREATE TABLE MaintenanceWorkers (
    worker_id INT PRIMARY KEY,
    worker_name VARCHAR(255),
    worker_role VARCHAR(50)
);

CREATE TABLE train_schedule (
    train_id INT PRIMARY KEY,
    train_name VARCHAR(255),
    departure_time TIME,
    arrival_time TIME
);

CREATE TABLE track_maintenance_records (
    record_id INT PRIMARY KEY,
    track_id INT,
    maintenance_type VARCHAR(255),
    maintenance_date DATE
);

CREATE TABLE track_information (
    track_id INT PRIMARY KEY,
    track_name VARCHAR(255),
    track_length FLOAT,
    maintenance_status VARCHAR(255)
);

CREATE TABLE station_information (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(255),
    station_location VARCHAR(255),
    station_capacity INT
);

CREATE TABLE Track { 
    track_id INT PRIMARY KEY, 
    track_name VARCHAR(255), 
    track_length DECIMAL(10,2), 
    track_type VARCHAR(255) 
}

CREATE TABLE Station { 
    station_id INT PRIMARY KEY, 
    station_name VARCHAR(255), 
    location VARCHAR(255), 
    platform_count INT 
}
