CREATE TABLE rail_inventory (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    quantity INT,
    price DECIMAL(10, 2)
);

CREATE TABLE supply_chain (
    id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    product_id INT,
    received_date DATE
);

CREATE TABLE inventory (
    inventory_id INT PRIMARY KEY,
