tables.table_schema:
CREATE TABLE trains (
    train_id UUID PRIMARY KEY,
    train_name VARCHAR(50) NOT NULL,
    safety_rating INT NOT NULL
);

CREATE TABLE infrastructure (
    infra_id UUID PRIMARY KEY,
    infra_type VARCHAR(50) NOT NULL,
    compliance_status BOOLEAN
);

CREATE TABLE TrainRecords (
    train_id INT PRIMARY KEY,
    train_type VARCHAR(50),
    inspection_date DATE,
    inspection_location VARCHAR(100)
);

CREATE TABLE InfrastructureRecords (
    infrastructure_id INT PRIMARY KEY,
    infrastructure_type VARCHAR(50),
    inspection_date DATE,
    inspection_location VARCHAR(100)
);

CREATE TABLE trains (
    id INT PRIMARY KEY,
    train_name VARCHAR(50) NOT NULL,
    route VARCHAR(100) NOT NULL,
    departure_time TIMESTAMP
);

CREATE TABLE infrastructure (
    infra_id INT PRIMARY KEY,
    infra_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    maintenance_schedule TEXT
);
