tables.table_schema:
CREATE TABLE astronaut_profiles (
  astronaut_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  certification VARCHAR(50),
  experience_years INT
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(50) NOT NULL,
  certification_authority VARCHAR(50)
);

CREATE TABLE Astronauts (
    astronaut_id INT PRIMARY KEY,
    name VARCHAR(50),
    nationality VARCHAR(50),
    birthdate DATE
);

CREATE TABLE Certifications (
    certification_id INT PRIMARY KEY,
    astronaut_id INT,
    certification_type VARCHAR(50),
    certification_date DATE
);

CREATE TABLE Experience (
    experience_id INT PRIMARY KEY,
    astronaut_id INT,
    organization VARCHAR(100),
    start_date DATE,
    end_date DATE
);

CREATE TABLE Missions (
    mission_id INT PRIMARY KEY,
    mission_name VARCHAR(100),
    launch_date DATE,
    landing_date DATE
);

CREATE TABLE Spacecraft (
    spacecraft_id INT PRIMARY KEY,
    spacecraft_name VARCHAR(100),
    spacecraft_type VARCHAR(50),
    manufacturer VARCHAR(100)
);
