tables.table_schema:
CREATE TABLE spacecraft_parts (
    part_id INT PRIMARY KEY,
    part_name TEXT,
    part_quantity INT,
    part_location TEXT
);

CREATE TABLE spacecraft_parts (
    id INT PRIMARY KEY,
    part_name TEXT,
    part_number TEXT,
    quantity INT
);

CREATE TABLE components (
    id INT PRIMARY KEY,
    component_name TEXT,
    part_id INT,
    location TEXT
);

CREATE TABLE maintenance_logs (
    id INT PRIMARY KEY,
    maintenance_date DATE,
    part_id INT,
    technician_name TEXT
);

CREATE TABLE spacecraft_parts (
  part_id INT PRIMARY KEY,
  part_name VARCHAR(50),
  part_category VARCHAR(50),
  quantity INT
);

CREATE TABLE maintenance_records (
  record_id INT PRIMARY KEY,
  component_id INT REFERENCES spacecraft_components(component_id),
  maintenance_date DATE,
  maintenance_type VARCHAR(50)
);

CREATE TABLE replacement_parts (
  replacement_id INT PRIMARY KEY,
  part_id INT REFERENCES spacecraft_parts(part_id),
  replacement_date DATE,
  technician_name VARCHAR(50)
);

CREATE TABLE user_accounts (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(50),
  password VARCHAR(50)
);
