tables.table_schema:
CREATE TABLE space_missions (
    mission_id INT PRIMARY KEY,
    mission_name VARCHAR(100),
    launch_date DATE,
    mission_status VARCHAR(20)
);

CREATE TABLE scientific_discoveries (
    discovery_id INT PRIMARY KEY,
    discovery_name VARCHAR(100),
    discovery_date DATE,
    discovery_description TEXT
);

CREATE TABLE SpaceMissions (
    mission_id INT PRIMARY KEY,
    mission_name VARCHAR(255),
    launch_date DATE,
    mission_status VARCHAR(50)
);

CREATE TABLE ScientificDiscoveries (
    discovery_id INT PRIMARY KEY,
    discovery_name VARCHAR(255),
    discovery_date DATE,
    discovery_description TEXT
);

CREATE TABLE MissionDiscoveries (
    mission_id INT,
    discovery_id INT,
    PRIMARY KEY (mission_id, discovery_id)
);
