tables.table_schema:
CREATE TABLE communication_logs (
    log_id SERIAL PRIMARY KEY,
    mission_control_message TEXT NOT NULL,
    astronaut_message TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE astronauts (
    astronaut_id SERIAL PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    mission_count INT NOT NULL
);

CREATE TABLE mission_control (
    control_id INT PRIMARY KEY,
    control_name VARCHAR(50),
    location VARCHAR(50)
);

CREATE TABLE astronauts (
    astronaut_id INT PRIMARY KEY,
    astronaut_name VARCHAR(50),
    role VARCHAR(50),
    mission_id INT
);

CREATE TABLE missions (
    mission_id SERIAL PRIMARY KEY,
    mission_name TEXT NOT NULL,
    start_date TIMESTAMP,
    end_date TIMESTAMP,
);

CREATE TABLE mission_control (
    control_id SERIAL PRIMARY KEY,
    control_name TEXT NOT NULL,
    location TEXT,
    contact_number TEXT,
);
