tables.table_schema:
CREATE TABLE bicycle_models (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    brand VARCHAR(255),
    year INT
);

CREATE TABLE bicycle_prices (
    model_id INT,
    price DECIMAL(10,2)
);

CREATE TABLE bike_reviews (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    reviewer_name VARCHAR(255) NOT NULL,
    review_text TEXT
);

CREATE TABLE Prices (
    price_id INTEGER PRIMARY KEY,
    model_id INTEGER REFERENCES BicycleModels(model_id),
    price_date DATE,
    price DECIMAL(10,2)
);

CREATE TABLE Manufactures (
    manufacturer_id INTEGER PRIMARY KEY,
    manufacturer_name VARCHAR(50) NOT NULL
);

CREATE TABLE ModelManufacturers (
    model_id INTEGER REFERENCES BicycleModels(model_id),
    manufacturer_id INTEGER REFERENCES Manufactures(manufacturer_id)
);
