tables.table_schema:
CREATE TABLE warranty_info (
    id INT PRIMARY KEY,
    warranty_period INT,
    terms VARCHAR(255),
    registration_date TIMESTAMP
);

CREATE TABLE warranty_information (
  warranty_id INT PRIMARY KEY,
  warranty_period VARCHAR(50) NOT NULL,
  warranty_terms TEXT,
  registration_details VARCHAR(100) NOT NULL
);

CREATE TABLE customer_information (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  customer_email VARCHAR(100) NOT NULL,
  customer_phone_number VARCHAR(20)
);

CREATE TABLE warranty_periods (
    warranty_id INT PRIMARY KEY,
    period_start DATE,
    period_end DATE,
    terms TEXT
);

CREATE TABLE warranty_terms (
    term_id INT PRIMARY KEY,
    term_description TEXT
);

CREATE TABLE warranty_periods (
  id SERIAL PRIMARY KEY,
  warranty_name VARCHAR(255) NOT NULL,
  duration INT NOT NULL
);

CREATE TABLE warranty_terms (
  id SERIAL PRIMARY KEY,
  term_id INT,
  term_description VARCHAR(500) NOT NULL
);
