tables.table_schema:
CREATE TABLE customer_feedback (
  feedback_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  feedback_text TEXT,
  rating INT
);

CREATE TABLE CustomerFeedback (
   customer_id UUID PRIMARY KEY,
   feedback_text TEXT,
   rating FLOAT
);

CREATE TABLE ServiceReviews (
   review_id UUID PRIMARY KEY,
   service_type VARCHAR,
   review_text TEXT,
   review_date DATE
);

CREATE TABLE customers ( 
    customer_id SERIAL PRIMARY KEY, 
    customer_name VARCHAR(255), 
    customer_email VARCHAR(255) 
 );

CREATE TABLE reviews ( 
    review_id SERIAL PRIMARY KEY, 
    customer_id INT REFERENCES customers(customer_id), 
    review_text TEXT, 
    review_date TIMESTAMP 
 );

CREATE TABLE services ( 
    service_id SERIAL PRIMARY KEY, 
    service_name VARCHAR(255), 
    service_description TEXT 
 );

CREATE TABLE CustomerFeedback (
  feedback_id SERIAL PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  feedback_text TEXT NOT NULL,
  rating INT NOT NULL
);

CREATE TABLE ReviewAnalysis (
  analysis_id SERIAL PRIMARY KEY,
  feedback_id INT REFERENCES CustomerFeedback(feedback_id),
  analysis_results JSON
);

CREATE TABLE ServiceImprovement (
  improvement_id SERIAL PRIMARY KEY,
  area_to_improve VARCHAR(50) NOT NULL,
  recommended_action TEXT
);

CREATE TABLE ServiceQuality (
  service_id SERIAL PRIMARY KEY,
  service_name VARCHAR(50) NOT NULL,
  quality_rating INT NOT NULL
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  customer_email VARCHAR(100)
);

CREATE TABLE Cars (
  car_id INT PRIMARY KEY,
  car_make VARCHAR(50),
  car_model VARCHAR(50),
  car_year INT
);

CREATE TABLE ServiceProviders (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(50),
  provider_location VARCHAR(100)
);

CREATE TABLE FeedbackAnalysis (
  analysis_id INT PRIMARY KEY,
  review_id INT,
  analysis_result TEXT
);
