tables.table_schema:
CREATE TABLE Electric_Vehicle_Batteries (
    battery_id UUID PRIMARY KEY,
    manufacturer TEXT,
    capacity DECIMAL,
    cycle_life INTEGER
);

CREATE TABLE battery_metrics (
    battery_id INT PRIMARY KEY,
    manufacturer VARCHAR(50),
    capacity DECIMAL,
    charging_speed INT
);

CREATE TABLE charging_stations (
    station_id INT PRIMARY KEY,
    location VARCHAR(100),
    num_chargers INT,
    operator VARCHAR(50)
);
