tables.table_schema:
CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT,
    phone_number TEXT
);

CREATE TABLE charging_station_stats (
  id SERIAL PRIMARY KEY,
  station_id INT NOT NULL REFERENCES charging_stations(id),
  usage_count INT DEFAULT 0,
  last_used TIMESTAMP
);

CREATE TABLE charging_station_reviews (
  id SERIAL PRIMARY KEY,
  station_id INT NOT NULL REFERENCES charging_stations(id),
  review_text TEXT,
  rating INT CHECK (rating >= 1 AND rating <= 5)
);

CREATE TABLE charging_station_images (
  id SERIAL PRIMARY KEY,
  station_id INT NOT NULL REFERENCES charging_stations(id),
  image_url VARCHAR(255) NOT NULL
);

CREATE TABLE ChargingStationOwners (
    owner_id INT PRIMARY KEY,
    station_id INT REFERENCES ChargingStationLocations(station_id),
    owner_name TEXT NOT NULL,
    contact_email TEXT NOT NULL
);

CREATE TABLE ChargingStationLogs (
    log_id INT PRIMARY KEY,
    station_id INT REFERENCES ChargingStationLocations(station_id),
    log_description TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE ChargingStationReviews (
    review_id INT PRIMARY KEY,
    station_id INT REFERENCES ChargingStationLocations(station_id),
    review_score FLOAT,
    review_comment TEXT
);
