tables.table_schema:
CREATE TABLE electric_vehicle_components (component_id INT PRIMARY KEY,
component_name VARCHAR(255) NOT NULL,
component_type VARCHAR(50) NOT NULL,
warranty_start_date DATE,
warranty_end_date DATE
);

CREATE TABLE component_warranty (warranty_id INT PRIMARY KEY,
component_id INT,
warranty_type VARCHAR(50) NOT NULL,
warranty_duration INT,
FOREIGN KEY (component_id) REFERENCES electric_vehicle_components(component_id)
);

CREATE TABLE electric_vehicle_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(50),
    warranty_period INT
);

CREATE TABLE vehicle_warranty (
    vehicle_id INT PRIMARY KEY,
    warranty_start_date DATE,
    warranty_end_date DATE
);

CREATE TABLE component_warranty (
    component_id INT,
    vehicle_id INT,
    warranty_status VARCHAR(20)
);

CREATE TABLE WarrantyProviders (
    id UUID PRIMARY KEY,
    provider_name VARCHAR(255) NOT NULL,
    contact_email VARCHAR(255) NOT NULL,
    contact_number VARCHAR(20) NOT NULL
);

CREATE TABLE Manufacturer (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255) NOT NULL,
    contact_number VARCHAR(15) NOT NULL
);

CREATE TABLE ComponentInventory (
    component_id INT PRIMARY KEY,
    manufacturer_id INT NOT NULL,
    available_quantity INT NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE VehicleOwnership (
    vehicle_id INT PRIMARY KEY,
    owner_name VARCHAR(255) NOT NULL,
    owner_contact VARCHAR(15) NOT NULL,
    purchase_date DATE
);
