tables.table_schema:
CREATE TABLE electric_vehicles (
	ev_id SERIAL PRIMARY KEY,
	model VARCHAR(50) NOT NULL,
	year INT NOT NULL,
	resale_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE owners (
	owner_id SERIAL PRIMARY KEY,
	ev_id INT NOT NULL,
	owner_name VARCHAR(50) NOT NULL,
	ownership_start_date DATE NOT NULL
);

CREATE TABLE owners (
    owner_id SERIAL PRIMARY KEY,
    owner_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE Manufacturer (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(50),
    headquarters_location VARCHAR(100),
    founding_year INT
);

CREATE TABLE Reseller (
    reseller_id INT PRIMARY KEY,
    reseller_name VARCHAR(50),
    address VARCHAR(100),
    contact_number VARCHAR(20)
);

CREATE TABLE EVManufacturers (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(50),
    headquarters VARCHAR(100),
    founding_year INT
);

CREATE TABLE EVUsers (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    age INT,
    location VARCHAR(100)
);
