tables.table_schema:
CREATE TABLE vehicle_parts (
    part_id SERIAL PRIMARY KEY,
    part_name VARCHAR(50) NOT NULL,
    vehicle_model VARCHAR(50) NOT NULL,
    part_quantity INT NOT NULL
);

CREATE TABLE part_suppliers (
    supplier_id SERIAL PRIMARY KEY,
    supplier_name VARCHAR(50) NOT NULL,
    supplier_address VARCHAR(100) NOT NULL,
    part_id INT REFERENCES vehicle_parts(part_id)
);

CREATE TABLE suppliers (
   supplier_id INT PRIMARY KEY,
   supplier_name VARCHAR(50),
   contact_number VARCHAR(15),
   email VARCHAR(50)
);

CREATE TABLE spare_parts (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(255),
    quantity INT,
    price DECIMAL
); 

CREATE TABLE vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_model VARCHAR(255),
    year INT,
    manufacturer VARCHAR(255)
); 

CREATE TABLE part_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    description TEXT
); 

CREATE TABLE inventory (
    inventory_id INT PRIMARY KEY,
    part_id INT,
    quantity INT,
    FOREIGN KEY (part_id) REFERENCES spare_parts(part_id)
);

CREATE TABLE electric_vehicles_parts (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(50),
    part_description TEXT,
    part_quantity INT
);

CREATE TABLE electric_vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_model VARCHAR(50),
    vehicle_year INT,
    vehicle_color VARCHAR(20)
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(50),
    supplier_address TEXT,
    supplier_contact VARCHAR(20)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(50),
    customer_phone VARCHAR(20)
);
