tables.table_schema:
CREATE TABLE smart_meters (
    meter_id INT PRIMARY KEY,
    location VARCHAR(50),
    date TIMESTAMP,
    power_consumption DECIMAL
);

CREATE TABLE smart_meter_data (
    meter_id INT PRIMARY KEY,
    reading_time TIMESTAMP,
    consumption DECIMAL
);

CREATE TABLE meter_location (
    meter_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    city VARCHAR(100),
    state VARCHAR(100)
);

CREATE TABLE energy_analysis (
    analysis_id INT PRIMARY KEY,
    meter_id INT,
    analysis_date DATE,
    analysis_result VARCHAR(255)
);

CREATE TABLE energy_consumption (
  id INT PRIMARY KEY,
  meter_id INT,
  date TIMESTAMP,
  usage DECIMAL
);

CREATE TABLE meter_readings (
  id INT PRIMARY KEY,
  meter_id INT,
  date TIMESTAMP,
  reading DECIMAL
);
