tables.table_schema:
CREATE TABLE building_energy_data (
  building_id INT PRIMARY KEY,
  energy_usage DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE building_info (
  building_id INT PRIMARY KEY,
  building_name VARCHAR,
  building_location VARCHAR
);

CREATE TABLE efficiency_improvements (
  improvement_id INT PRIMARY KEY,
  building_id INT,
  improvement_description VARCHAR
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    building_location VARCHAR(255) NOT NULL
);

CREATE TABLE energy_readings (
    reading_id INT PRIMARY KEY,
    building_id INT NOT NULL,
    date_time TIMESTAMP NOT NULL,
    energy_usage FLOAT NOT NULL
);

CREATE TABLE energy_zones (
    zone_id INT PRIMARY KEY,
    building_id INT NOT NULL,
    zone_name VARCHAR(255) NOT NULL
);

CREATE TABLE efficiency_improvements (
    improvement_id INT PRIMARY KEY,
    building_id INT NOT NULL,
    improvement_description TEXT
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    location VARCHAR(255),
    construction_year INT
);

CREATE TABLE energy_meters (
    meter_id INT PRIMARY KEY,
    building_id INT,
    meter_type ENUM('electricity', 'water', 'gas'),
    installation_date DATE
);

CREATE TABLE energy_measurements (
    measurement_id INT PRIMARY KEY,
    meter_id INT,
    measurement_value DECIMAL,
    measurement_date TIMESTAMP
);

CREATE TABLE energy_usage_patterns (
    pattern_id INT PRIMARY KEY,
    building_id INT,
    pattern_type VARCHAR(255),
    pattern_description TEXT
);

CREATE TABLE efficiency_improvements (
    improvement_id INT PRIMARY KEY,
    building_id INT,
    improvement_type VARCHAR(255),
    improvement_date DATE
);
