CREATE TABLE EnergyConsumptionData (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMPTZ,
    energy_usage DECIMAL
);

CREATE TABLE ForecastedEnergyNeeds (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMPTZ,
    forecasted_energy DECIMAL
);

CREATE TABLE EnergyConsumption (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    location VARCHAR(255),
    energy_usage FLOAT
);

CREATE TABLE EnergyForecast (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    forecast_date DATE,
    forecasted_usage FLOAT
);

CREATE TABLE EnergyProviders (
    id INT PRIMARY KEY,
    provider_name VARCHAR(255),
    provider_location VARCHAR(255)
);

CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50),
    energy_type VARCHAR(50),
    capacity FLOAT
);

CREATE TABLE energy_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50),
    location VARCHAR(50),
    contact_info VARCHAR(100)
);
