tables.table_schema:
CREATE TABLE energy_efficiency_ratings (
  appliance_id INT PRIMARY KEY,
  appliance_name VARCHAR(255),
  energy_rating VARCHAR(10),
  efficiency_score INT
);

CREATE TABLE appliances (
  appliance_id INT PRIMARY KEY,
  appliance_name VARCHAR(255),
  energy_rating VARCHAR(10)
);

CREATE TABLE consumers (
  consumer_id INT PRIMARY KEY,
  consumer_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE appliance_makes (
  make_id INT PRIMARY KEY,
  make_name VARCHAR(50)
);

CREATE TABLE appliance_types (
  type_id INT PRIMARY KEY,
  type_name VARCHAR(50)
);

CREATE TABLE EnergyEfficiencyRatings (
   appliance_id INT PRIMARY KEY,
   appliance_name VARCHAR(255),
   efficiency_rating DECIMAL(4,2)
);

CREATE TABLE ApplianceCategories (
   category_id INT PRIMARY KEY,
   category_name VARCHAR(255)
);
