tables.table_schema:
CREATE TABLE energy_audit_reports (
  report_id SERIAL PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  report_date DATE NOT NULL,
  energy_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE audit_notes (
  note_id SERIAL PRIMARY KEY,
  report_id INT REFERENCES energy_audit_reports(report_id),
  note_text TEXT NOT NULL,
  note_date TIMESTAMP NOT NULL
);

CREATE TABLE energy_audit_progress (
    progress_id UUID PRIMARY KEY,
    report_id UUID REFERENCES energy_audit_reports (report_id),
    progress_date DATE,
    reduction_percentage DECIMAL(5,2)
);

CREATE TABLE energy_audit_reports (
  report_id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  audit_date DATE NOT NULL,
  energy_consumption FLOAT NOT NULL
);

CREATE TABLE audit_data (
  data_id INT PRIMARY KEY,
  report_id INT NOT NULL,
  energy_type VARCHAR(50) NOT NULL,
  energy_usage FLOAT NOT NULL
);

CREATE TABLE company_information (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  address VARCHAR(255) NOT NULL,
  contact_name VARCHAR(100) NOT NULL
);

CREATE TABLE progress_tracker (
  tracker_id INT PRIMARY KEY,
  data_id INT NOT NULL,
  progress_date DATE NOT NULL,
  progress_status VARCHAR(50) DEFAULT 'Pending'
);
