CREATE TABLE energy_rebates(
  id INT PRIMARY KEY,
  business_name VARCHAR(255),
  rebate_amount DECIMAL(10, 2),
  rebate_date DATE
);

CREATE TABLE incentives(
  id INT PRIMARY KEY,
  incentive_type VARCHAR(50),
  incentive_description TEXT,
  incentive_amount DECIMAL(10, 2)
);

CREATE TABLE EnergyRebates (
 rebate_id UUID PRIMARY KEY,
 business_name TEXT,
 rebate_amount DECIMAL
);

CREATE TABLE Incentives (
 incentive_id UUID PRIMARY KEY,
 incentive_type TEXT,
 incentive_value DECIMAL
);
