tables.table_schema:
CREATE TABLE community_energy_initiatives (
    id INT PRIMARY KEY,
    initiative_name VARCHAR(255),
    location VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    community_id INT,
    consumption_date DATE,
    electricity_consumed DECIMAL,
    water_consumed DECIMAL
);

CREATE TABLE sustainability_goals (
    id INT PRIMARY KEY,
    goal_name VARCHAR(255),
    target_date DATE,
    target_metric VARCHAR(255)
);

CREATE TABLE community_feedback (
    id INT PRIMARY KEY,
    initiative_id INT,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE communities (
    community_id INT PRIMARY KEY,
    community_name VARCHAR,
    region VARCHAR
);

CREATE TABLE organizations (
    organization_id INT PRIMARY KEY,
    organization_name VARCHAR,
    contact_email VARCHAR
);
