tables.table_schema:
CREATE TABLE energy_technologies (
  technology_id UUID PRIMARY KEY,
  technology_name VARCHAR(255) NOT NULL,
  energy_saving_rating INT
);

CREATE TABLE energy_consumption (
  consumption_id UUID PRIMARY KEY,
  technology_id UUID NOT NULL,
  energy_consumed DECIMAL,
  date_recorded DATE
);

CREATE TABLE EnergySavingTechnologies (
 id SERIAL PRIMARY KEY,
 technology_name VARCHAR(50) NOT NULL,
 efficiency_rating FLOAT NOT NULL
);

CREATE TABLE EnergyConsumption (
    technology_id INT PRIMARY KEY,
    technology_name VARCHAR(255),
    energy_saved DECIMAL
);
