tables.table_schema:
CREATE TABLE nuclear_power_plants (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100),
    capacity_mw INT,
    safety_rating VARCHAR(20)
);

CREATE TABLE nuclear_power_plants (
    plant_id INT PRIMARY KEY,
    location VARCHAR(100),
    capacity DECIMAL,
    safety_rating VARCHAR(20)
);

CREATE TABLE plant_safety_reports (
    report_id INT PRIMARY KEY,
    plant_id INT REFERENCES nuclear_power_plants(plant_id),
    report_date DATE,
    safety_issue VARCHAR(200)
);

CREATE TABLE plant_maintenance_logs (
    log_id INT PRIMARY KEY,
    plant_id INT REFERENCES nuclear_power_plants(plant_id),
    log_date DATE,
    maintenance_details TEXT
);
