tables.table_schema:
CREATE TABLE Experimental_Data ( 
 reactor_id INT PRIMARY KEY, 
 temperature FLOAT NOT NULL, 
 pressure FLOAT NOT NULL 
 );

CREATE TABLE Simulation_Results ( 
 simulation_id INT PRIMARY KEY, 
 reactor_id INT NOT NULL, 
 efficiency FLOAT NOT NULL, 
 FOREIGN KEY (reactor_id) REFERENCES Experimental_Data(reactor_id) 
 );

CREATE TABLE SimulationResults (
  simulation_id INT,
  reactor_id INT,
  simulation_time TIMESTAMP,
  simulation_output TEXT
);

CREATE TABLE Researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  researcher_email VARCHAR(50)
);

CREATE TABLE Reactors (
  reactor_id INT PRIMARY KEY,
  reactor_name VARCHAR(50),
  reactor_location VARCHAR(100),
  reactor_type VARCHAR(50)
);
