tables.table_schema:
CREATE TABLE regulatory_filings (
    filing_id INT PRIMARY KEY,
    filing_date DATE,
    document_name VARCHAR(255),
    plant_id INT
);

CREATE TABLE compliance_documents (
    document_id INT PRIMARY KEY,
    document_name VARCHAR(255),
    plant_id INT,
    expiration_date DATE
);

CREATE TABLE nuclear_power_plant_regulatory_filings (
   filing_id INT PRIMARY KEY,
   filing_date DATE,
   filing_type VARCHAR(255),
   filing_description TEXT
);

CREATE TABLE compliance_documents (
   document_id INT PRIMARY KEY,
   document_name VARCHAR(255),
   document_type VARCHAR(100),
   document_content BYTEA
);

CREATE TABLE plant_information (
   plant_id INT PRIMARY KEY,
   plant_name VARCHAR(255),
   plant_location VARCHAR(100),
   plant_capacity FLOAT
);

CREATE TABLE plant_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT,
    regulation_type VARCHAR(50)
);
