tables.table_schema:
CREATE TABLE Nuclear_Incidents (
    incident_id INT NOT NULL PRIMARY KEY,
    incident_location VARCHAR(255),
    incident_date DATE,
    radiation_level DECIMAL(10,3)
);

CREATE TABLE Radiation_Levels (
    radiation_id INT NOT NULL PRIMARY KEY,
    location VARCHAR(255),
    radiation_level DECIMAL(10,3),
    timestamp TIMESTAMP
);

CREATE TABLE Evacuation_Guidelines (
    evacuation_id INT NOT NULL PRIMARY KEY,
    evacuation_location VARCHAR(255),
    evacuation_route VARCHAR(255),
    evacuation_instructions TEXT
);

CREATE TABLE Emergency_Teams (
    team_id INT NOT NULL PRIMARY KEY,
    team_name VARCHAR(255),
    team_leader VARCHAR(255),
    team_phone_number VARCHAR(20)
);

CREATE TABLE Access_Logs (
    log_id INT NOT NULL PRIMARY KEY,
    user_id INT,
    access_timestamp TIMESTAMP,
    accessed_table VARCHAR(255)
);
