tables.table_schema:
CREATE TABLE AccidentCauses (
    id INT PRIMARY KEY,
    accident_id INT,
    cause VARCHAR(255),
    FOREIGN KEY (accident_id) REFERENCES NuclearAccidents(id)
);

CREATE TABLE accidents (
    accident_id SERIAL PRIMARY KEY,
    accident_date DATE,
    accident_description TEXT
);

CREATE TABLE causes (
    cause_id SERIAL PRIMARY KEY,
    accident_id INT REFERENCES accidents(accident_id),
    cause_description TEXT
);

CREATE TABLE prevention_lessons (
    lesson_id SERIAL PRIMARY KEY,
    accident_id INT REFERENCES accidents(accident_id),
    lesson_description TEXT
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    location_name TEXT,
    country TEXT
);
