tables.table_schema:
CREATE TABLE nuclear_energy_security_audit (
    id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(50),
    findings TEXT
);

CREATE TABLE data_encryption (
    encryption_id INT PRIMARY KEY,
    encryption_type VARCHAR(50),
    encryption_key BLOB,
    data_type VARCHAR(50)
);

CREATE TABLE SecurityAudit (
    audit_id INT PRIMARY KEY,
    audit_date TIMESTAMP,
    auditor_name VARCHAR(50),
    audit_result VARCHAR(100)
);

CREATE TABLE Encryption (
    encryption_id INT PRIMARY KEY,
    encryption_date TIMESTAMP,
    encryption_type VARCHAR(50),
    encryption_key VARCHAR(100)
);

CREATE TABLE NuclearFacility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100),
    facility_location VARCHAR(200),
    facility_status VARCHAR(50)
);

CREATE TABLE SensitiveInformation (
    info_id INT PRIMARY KEY,
    info_type VARCHAR(50),
    info_description TEXT,
    info_encrypted BOOL
);

CREATE TABLE Nuclear_Users ( 
    user_id INT PRIMARY KEY, 
    username VARCHAR(50) NOT NULL, 
    password VARCHAR(100) NOT NULL, 
    role VARCHAR(20) NOT NULL 
);

CREATE TABLE Nuclear_Employee_Details ( 
    employee_id INT PRIMARY KEY, 
    employee_name VARCHAR(50) NOT NULL, 
    department VARCHAR(50) NOT NULL, 
    salary DECIMAL(10,2) NOT NULL 
);

CREATE TABLE Nuclear_Data_Encryption ( 
    encryption_id INT PRIMARY KEY, 
    encryption_key VARCHAR(100) NOT NULL, 
    algorithm VARCHAR(50) NOT NULL 
);

CREATE TABLE Nuclear_Sensitive_Info ( 
    info_id INT PRIMARY KEY, 
    info_description TEXT NOT NULL, 
    encryption_id INT NOT NULL, 
    FOREIGN KEY (encryption_id) REFERENCES Nuclear_Data_Encryption(encryption_id) 
);
