tables.table_schema:
CREATE TABLE wind_farm_locations (
    location_id INT PRIMARY KEY,
    latitude DECIMAL(10, 8),
    longitude DECIMAL(11, 8),
    wind_speed DECIMAL(5, 2)
);

CREATE TABLE WindFarmLocations (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    average_wind_speed DECIMAL
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    latitude NUMERIC,
    longitude NUMERIC,
    elevation NUMERIC
);

CREATE TABLE wind_speed (
    speed_id SERIAL PRIMARY KEY,
    location_id INT REFERENCES locations(location_id),
    speed NUMERIC
);

CREATE TABLE wind_direction (
    direction_id SERIAL PRIMARY KEY,
    location_id INT REFERENCES locations(location_id),
    direction VARCHAR(50)
);

CREATE TABLE wind_power (
    power_id SERIAL PRIMARY KEY,
    location_id INT REFERENCES locations(location_id),
    power_output NUMERIC
);

CREATE TABLE WindFarms (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  latitude FLOAT NOT NULL,
  longitude FLOAT NOT NULL
);

CREATE TABLE Turbines (
  id INT PRIMARY KEY,
  model VARCHAR(255) NOT NULL,
  height FLOAT NOT NULL,
  capacity FLOAT NOT NULL
);

CREATE TABLE Locations (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  latitude FLOAT NOT NULL,
  longitude FLOAT NOT NULL
);

CREATE TABLE WindSpeed (
  location_id INT,
  date DATE,
  speed FLOAT NOT NULL,
  PRIMARY KEY (location_id, date)
);

CREATE TABLE Analysis (
  id INT PRIMARY KEY,
  location_id INT NOT NULL,
  speed FLOAT NOT NULL,
  capacity FLOAT NOT NULL
);
