tables.table_schema:
CREATE TABLE maintenance_schedule (
  inverter_id INT,
  maintenance_date DATE,
  maintenance_details TEXT
);

CREATE TABLE InverterMaintenanceSchedule (
    inverter_id INT PRIMARY KEY,
    last_maintenance_date DATE,
    maintenance_frequency INT,
    next_maintenance_date DATE
);

CREATE TABLE technicians (
    technician_id INT PRIMARY KEY,
    name VARCHAR(50),
    phone_number VARCHAR(15),
    email VARCHAR(50)
);

CREATE TABLE tbl_inverters (
    inverter_id INT PRIMARY KEY,
    inverter_name VARCHAR(255),
    location VARCHAR(255),
    next_maintenance_date DATE
);

CREATE TABLE tbl_maintenance_logs (
    log_id INT PRIMARY KEY,
    inverter_id INT,
    maintenance_date DATE,
    maintenance_description TEXT
);

CREATE TABLE tbl_repair_history (
    repair_id INT PRIMARY KEY,
    inverter_id INT,
    repair_date DATE,
    repair_description TEXT
);

CREATE TABLE tbl_maintenance_schedule (
    schedule_id INT PRIMARY KEY,
    inverter_id INT,
    scheduled_date DATE
);

CREATE TABLE tbl_engineers (
    engineer_id INT PRIMARY KEY,
    engineer_name VARCHAR(255),
    qualification VARCHAR(255)
);
