tables.table_schema:
CREATE TABLE solar_panels (
    panel_id SERIAL PRIMARY KEY,
    location VARCHAR(100) NOT NULL,
    capacity DECIMAL(10,2) NOT NULL,
    installation_date DATE
);

CREATE TABLE solar_panel_locations (
  id SERIAL PRIMARY KEY,
  location_name VARCHAR(100) NOT NULL,
  latitude FLOAT NOT NULL,
  longitude FLOAT NOT NULL
);

CREATE TABLE panel_status (
  panel_id INT,
  status VARCHAR(20) NOT NULL,
  last_status_update TIMESTAMP,
  FOREIGN KEY (panel_id) REFERENCES solar_panel_locations(id)
);

CREATE TABLE panel_location (
    panel_id INT PRIMARY KEY,
    location_id INT,
    longitude FLOAT,
    latitude FLOAT
);

CREATE TABLE installation (
    installation_id INT PRIMARY KEY,
    panel_id INT,
    installation_date DATE,
    installer VARCHAR(50)
);

CREATE TABLE location (
    location_id INT PRIMARY KEY,
    city VARCHAR(50),
    state VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE panel_status (
    panel_id INT PRIMARY KEY,
    status VARCHAR(50),
    last_maintenance DATE
);

CREATE TABLE panels (
  panel_id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  capacity FLOAT NOT NULL,
  manufacturer VARCHAR(100) NOT NULL
);

CREATE TABLE installations (
  installation_id INT PRIMARY KEY,
  panel_id INT NOT NULL,
  installation_date DATE NOT NULL,
  technician VARCHAR(100) NOT NULL
);

CREATE TABLE projects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(100) NOT NULL,
  start_date DATE NOT NULL,
  end_date DATE NOT NULL
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(100) NOT NULL,
  email VARCHAR(255) NOT NULL,
  phone_number VARCHAR(15) NOT NULL
);
