tables.table_schema:
CREATE TABLE solar_technologies (
    technology_id SERIAL PRIMARY KEY,
    technology_name VARCHAR(50) NOT NULL,
    efficiency DECIMAL(5,2) NOT NULL
);

CREATE TABLE solar_cells (
    cell_id UUID PRIMARY KEY,
    technology VARCHAR(50),
    efficiency FLOAT,
    manufacturer VARCHAR(100)
);

CREATE TABLE solar_panels (
    panel_id UUID PRIMARY KEY,
    cell_id UUID REFERENCES solar_cells(cell_id),
    panel_area FLOAT,
    rating FLOAT
);

CREATE TABLE performance_data (
    data_id UUID PRIMARY KEY,
    panel_id UUID REFERENCES solar_panels(panel_id),
    date TIMESTAMP,
    production FLOAT
);

CREATE TABLE SolarCells (
  cell_id INT PRIMARY KEY,
  cell_technology VARCHAR(50) NOT NULL,
  efficiency FLOAT NOT NULL,
  date_installed DATE
);
