tables.table_schema:
CREATE TABLE EnergyConsumptionPatterns (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_consumed FLOAT
);

CREATE TABLE energy_consumption_patterns (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    energy_consumed FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE solar_panel_inventory (
    panel_id INT PRIMARY KEY,
    location VARCHAR(255),
    watts_generated_per_hour FLOAT,
    panel_type VARCHAR(50)
);

CREATE TABLE energy_consumption ( 
    id INT, 
    date DATE, 
    time TIME, 
    consumption DECIMAL 
);

CREATE TABLE solar_panels ( 
    panel_id INT, 
    location VARCHAR(255), 
    capacity DECIMAL, 
    installation_date DATE 
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR (50) UNIQUE NOT NULL,
    email VARCHAR (100) UNIQUE NOT NULL,
    password VARCHAR (100) NOT NULL
);

CREATE TABLE solar_panels (
    panel_id SERIAL PRIMARY KEY,
    panel_type VARCHAR (50) NOT NULL,
    installation_date DATE,
    power_rating DECIMAL(5,2) NOT NULL
);

CREATE TABLE power_plants (
    plant_id SERIAL PRIMARY KEY,
    plant_name VARCHAR (50) NOT NULL,
    location VARCHAR (100) NOT NULL,
    capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE EnergyConsumption (
    Date DATE,
    Time TIME,
    DeviceID INT,
    Consumption DECIMAL
);

CREATE TABLE SolarPanel (
    PanelID INT,
    PanelType VARCHAR(50),
    InstallationDate DATE,
    Efficiency DECIMAL
);

CREATE TABLE User (
    UserID INT,
    UserName VARCHAR(50),
    Email VARCHAR(100)
);

CREATE TABLE EnergyProduction (
    Date DATE,
    Time TIME,
    PanelID INT,
    Production DECIMAL
);

CREATE TABLE EnergyUsage (
    UserID INT,
    Date DATE,
    Usage DECIMAL
);
