tables.table_schema:
CREATE TABLE efficiency_records (
    record_id SERIAL PRIMARY KEY,
    system_id INT REFERENCES solar_power_storage_systems(system_id),
    record_date DATE,
    efficiency_percent FLOAT
);

CREATE TABLE SolarPowerSystems (
  id INT PRIMARY KEY,
  system_name VARCHAR(255),
  storage_capacity DECIMAL,
  efficiency FLOAT
);
