tables.table_schema:
CREATE TABLE Solar_Panel_Orientation (
    panel_id INT PRIMARY KEY,
    panel_type VARCHAR(50),
    orientation_angle FLOAT,
    efficiency_rating FLOAT
);

CREATE TABLE solar_panel_locations (
 id INT PRIMARY KEY,
 latitude FLOAT NOT NULL,
 longitude FLOAT NOT NULL,
 orientation_degrees INT
 );

CREATE TABLE solar_panel_efficiency (
 id INT PRIMARY KEY,
 panel_id INT NOT NULL,
 efficiency_rating FLOAT NOT NULL,
 energy_output FLOAT
 );

CREATE TABLE solar_panel_stats (
 id INT PRIMARY KEY,
 panel_id INT NOT NULL,
 power_output FLOAT NOT NULL,
 temperature_degrees FLOAT
 );

CREATE TABLE solar_panel_owners (
 id INT PRIMARY KEY,
 panel_id INT NOT NULL,
 owner_name VARCHAR(255) NOT NULL,
 contact_email VARCHAR(255)
 );

CREATE TABLE SolarPanels (
    id INT PRIMARY KEY,
    panel_name VARCHAR(50) NOT NULL,
    orientation VARCHAR(20) NOT NULL,
    efficiency FLOAT NOT NULL
);

CREATE TABLE SunlightData (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    sunlight_level FLOAT NOT NULL
);

CREATE TABLE PanelLocation (
    id INT PRIMARY KEY,
    panel_id INT NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE PanelElectricity (
    id INT PRIMARY KEY,
    panel_id INT NOT NULL,
    electricity_production FLOAT NOT NULL,
    production_date DATE NOT NULL
);

CREATE TABLE PanelMaintenance (
    id INT PRIMARY KEY,
    panel_id INT NOT NULL,
    maintenance_date DATE NOT NULL,
    maintenance_details TEXT
);
