tables.table_schema:
CREATE TABLE turbines (
    turbine_id INT PRIMARY KEY,
    turbine_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    maintenance_schedule TIMESTAMP NOT NULL
);

CREATE TABLE turbine (
   turbine_id UUID PRIMARY KEY,
   manufacturer STRING,
   model STRING,
   location STRING
);

CREATE TABLE maintenance_updates (
   update_id UUID PRIMARY KEY,
   maintenance_id UUID,
   update_date DATE,
   update_notes TEXT
);

CREATE TABLE WindTurbine { 
    turbine_id INT PRIMARY KEY, 
    location VARCHAR(255), 
    last_maintenance_date DATE, 
    next_maintenance_date DATE 
}

CREATE TABLE MaintenanceLogs { 
    log_id INT PRIMARY KEY, 
    schedule_id INT, 
    log_message TEXT, 
    log_date DATETIME 
}

CREATE TABLE Technicians { 
    technician_id INT PRIMARY KEY, 
    technician_name VARCHAR(255), 
    technician_email VARCHAR(255), 
    technician_phone VARCHAR(15) 
}
