tables.table_schema:
CREATE TABLE wind_farm_personnel ( 
    employee_id INT PRIMARY KEY, 
    employee_name VARCHAR(50), 
    training_status BOOLEAN 
);

CREATE TABLE training_certifications ( 
    certification_id INT PRIMARY KEY, 
    certification_name VARCHAR(50), 
    certification_expiry_date DATE 
);

CREATE TABLE employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(50),
  certification_status BOOLEAN
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(50),
  valid_period INT
);

CREATE TABLE training (
  training_id INT PRIMARY KEY,
  training_name VARCHAR(50),
  training_date DATE
);

CREATE TABLE training_materials (
    material_id INT PRIMARY KEY,
    material_title VARCHAR(100),
    material_type VARCHAR(50),
    material_link VARCHAR(255)
);

CREATE TABLE employee_certifications (
    cert_id INT PRIMARY KEY,
    employee_id INT REFERENCES wind_farm_personnel(id),
    certification_name VARCHAR(100),
    certification_date DATE
);

CREATE TABLE training_sessions (
    session_id INT PRIMARY KEY,
    session_date DATE,
    session_duration INT,
    trainer_name VARCHAR(50)
);
