tables.table_schema:
CREATE TABLE market_analysis (
  analysis_id INT PRIMARY KEY,
  analysis_date DATE,
  market_demand INT,
  wind_energy_demand INT
);

CREATE TABLE investment_plan (
  plan_id INT PRIMARY KEY,
  plan_date DATE,
  investment_amount DECIMAL,
  investment_duration INT
);

CREATE TABLE market_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    demand_forecast DECIMAL
);

CREATE TABLE energy_investments (
    investment_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    investment_amount DECIMAL
);

CREATE TABLE wind_energy (
    wind_id INT PRIMARY KEY,
    wind_location VARCHAR(255),
    energy_capacity DECIMAL
);

CREATE TABLE MarketTrends (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    trend_type VARCHAR(50),
    demand_forecast INT
);

CREATE TABLE WindFarms (
    id INT PRIMARY KEY,
    farm_name VARCHAR(50),
    location VARCHAR(100),
    capacity_mw INT
);

CREATE TABLE EnergyProduction (
    id INT PRIMARY KEY,
    farm_id INT,
    production_date DATE,
    energy_produced DECIMAL(10,2)
);

CREATE TABLE Investments (
    id INT PRIMARY KEY,
    investment_name VARCHAR(50),
    amount DECIMAL(10,2),
    investment_date DATE
);

CREATE TABLE market_trends (
    id SERIAL PRIMARY KEY,
    date DATE,
    trend_description TEXT
);

CREATE TABLE demand_forecasts (
    id SERIAL PRIMARY KEY,
    date DATE,
    forecasted_demand NUMERIC
);

CREATE TABLE wind_energy_investments (
    id SERIAL PRIMARY KEY,
    date DATE,
    investment_amount NUMERIC,
    investment_type VARCHAR(50)
);

CREATE TABLE energy_providers (
    id SERIAL PRIMARY KEY,
    provider_name VARCHAR(50),
    location VARCHAR(100),
    rating NUMERIC
);

CREATE TABLE market_analysis (
    id SERIAL PRIMARY KEY,
    date DATE,
    analysis_report TEXT
);
