    consumption_date DATE,
    consumed_units DECIMAL,
    PRIMARY KEY (city_name, consumption_date)
);

CREATE TABLE EnergyProduction (
    city_name VARCHAR(50),
    production_date DATE,
    produced_units DECIMAL,
    PRIMARY KEY (city_name, production_date)
);

