tables.table_schema:
CREATE TABLE hydropower_plants (
    location VARCHAR(255),
    capacity DECIMAL(10, 2),
    production_statistics VARCHAR(255)
);

CREATE TABLE plant_statistics (
    plant_id INT,
    production_year INT,
    electricity_produced DECIMAL(10, 2)
);

CREATE TABLE hydropower_plant_capacity (
    plant_id INT PRIMARY KEY,
    location_id INT,
    capacity_mw DECIMAL(10, 2),
    CONSTRAINT fk_location_id FOREIGN KEY (location_id) REFERENCES hydropower_plant_locations(location_id)
);

CREATE TABLE hydropower_plants (
    id INT PRIMARY KEY,
    location TEXT,
    capacity DECIMAL,
    production_statistics TEXT
);

CREATE TABLE plant_capacity (
    plant_id INT,
    capacity DECIMAL,
    date DATE
);

CREATE TABLE production_statistics (
    plant_id INT,
    production_amount DECIMAL,
    production_year INT
);

CREATE TABLE plant_locations (
    location_id INT PRIMARY KEY,
    location_name TEXT,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE hydropower_plant_locations (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    latitude DECIMAL(10, 6),
    longitude DECIMAL(10, 6)
);

CREATE TABLE hydropower_plant_capacity (
    id INT PRIMARY KEY,
    plant_id INT,
    capacity DECIMAL(10, 2),
    capacity_units VARCHAR(50)
);

CREATE TABLE hydropower_plant_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50)
);

CREATE TABLE hydropower_plant_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(100)
);
