tables.table_schema:
CREATE TABLE WeatherData (
  date date,
  temperature float,
  humidity float,
  wind_speed float
);

CREATE TABLE HydropowerGeneration (
  date date,
  generation_capacity float,
  actual_generation float
);

CREATE TABLE HydropowerGeneration (
    id INT PRIMARY KEY,
    date DATE,
    generated_power FLOAT,
    water_level FLOAT
);

CREATE TABLE WeatherHydropowerRelation (
    weather_id INT,
    hydropower_id INT,
    FOREIGN KEY (weather_id) REFERENCES WeatherData(id),
    FOREIGN KEY (hydropower_id) REFERENCES HydropowerGeneration(id)
);

CREATE TABLE Hydropower_Generation (
  generation_id INT PRIMARY KEY,
  location_id INT,
  generated_power DECIMAL,
  recorded_at TIMESTAMP
);

CREATE TABLE Impact_Analysis (
  analysis_id INT PRIMARY KEY,
  location_id INT,
  impact_description TEXT,
  analysis_date DATE
);

CREATE TABLE hydropower_generation (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  power_output FLOAT,
  water_level FLOAT
);

CREATE TABLE weather_station (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE hydropower_plant (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  capacity FLOAT
);

CREATE TABLE analysis_results (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  analysis TEXT
);
