tables.table_schema:
CREATE TABLE water_flow_levels (
	hydropower_reservoir_id INT PRIMARY KEY,
	water_level FLOAT,
	date_time TIMESTAMP
);

CREATE TABLE electricity_production (
	hydropower_reservoir_id INT,
	electricity_output FLOAT,
	date_time TIMESTAMP,
	FOREIGN KEY (hydropower_reservoir_id) REFERENCES water_flow_levels(hydropower_reservoir_id)
);

CREATE TABLE WaterFlowLevels (
    reservoir_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    water_level DECIMAL
);

CREATE TABLE Reservoirs (
    reservoir_id INT PRIMARY KEY,
    reservoir_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE ProductionOptimization (
    reservoir_id INT,
    timestamp TIMESTAMP,
    electricity_production DECIMAL,
    PRIMARY KEY (reservoir_id, timestamp)
);

CREATE TABLE HydropowerReservoir (
    reservoir_id INT PRIMARY KEY,
    reservoir_name VARCHAR(255),
    water_level DECIMAL
);

CREATE TABLE PowerConsumption (
    consumption_id INT PRIMARY KEY,
    consumption_date DATE,
    consumption_amount DECIMAL
);
