tables.table_schema:
CREATE TABLE hydropower_generation (
	hydropower_id UUID PRIMARY KEY,
	generation_date DATE,
	generation_time TIME,
	generation_amount FLOAT
);

CREATE TABLE hydropower_trends (
	trend_id UUID PRIMARY KEY,
	trend_timestamp TIMESTAMP,
	trend_description TEXT
);

CREATE TABLE hydropower_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  location VARCHAR(255),
  river_name VARCHAR(255)
);

CREATE TABLE hydropower_plants (
    id SERIAL PRIMARY KEY,
    plant_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    capacity FLOAT NOT NULL
);
