);

CREATE TABLE EnergyConsumption (
  ID INT PRIMARY KEY,
  Date DATE,
  Time TIME,
  EnergyUsage FLOAT
);

CREATE TABLE HydroelectricPowerPlants (
  PlantID INT PRIMARY KEY,
  PlantName VARCHAR(50),
  Location VARCHAR(100),
  Capacity FLOAT
);

CREATE TABLE WeatherForecast (
  Date DATE PRIMARY KEY,
  Temperature FLOAT,
  Precipitation FLOAT,
  WindSpeed FLOAT
);

CREATE TABLE EnergyProductionHistory (
  PlantID INT,
  Date DATE,
  EnergyGenerated FLOAT,
  CONSTRAINT fk_PlantID FOREIGN KEY (PlantID) REFERENCES HydroelectricPowerPlants(PlantID)
);
