tables.table_schema:
CREATE TABLE turbines (
    id INT,
    manufacturer VARCHAR,
    model VARCHAR,
    capacity INT
);

CREATE TABLE generators (
    id INT,
    turbine_id INT,
    type VARCHAR,
    output INT
);

CREATE TABLE performance_data (
    generator_id INT,
    timestamp TIMESTAMP,
    efficiency FLOAT,
    temperature INT
);

CREATE TABLE maintenance_logs (
    id INT,
    generator_id INT,
    description TEXT,
    log_date DATE
);

CREATE TABLE Turbines (
    turbine_id INT PRIMARY KEY,
    turbine_name VARCHAR(100),
    location_id INT,
    capacity DECIMAL
);

CREATE TABLE Generators (
    generator_id INT PRIMARY KEY,
    generator_name VARCHAR(100),
    turbine_id INT,
    status VARCHAR(50)
);

CREATE TABLE Locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100),
    city VARCHAR(100),
    country VARCHAR(100)
);
