tables.table_schema:
CREATE TABLE Environmental_Impacts(
    id INT PRIMARY KEY,
    energy_source VARCHAR(255),
    emissions_level DECIMAL,
    water_usage DECIMAL
);

CREATE TABLE Energy_Production_Methods(
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    efficiency DECIMAL,
    carbon_capture BOOLEAN
);

CREATE TABLE bioenergy_impact (
  id INT PRIMARY KEY,
  production_method VARCHAR(50),
  energy_output DECIMAL(10, 2),
  carbon_emissions DECIMAL(10, 2)
);

CREATE TABLE production_methods (
  id INT PRIMARY KEY,
  method_name VARCHAR(50),
  description TEXT
);

CREATE TABLE environmental_factors (
  id INT PRIMARY KEY,
  factor_name VARCHAR(50),
  impact_rating INT
);

CREATE TABLE environmental_impacts (
  impact_id SERIAL PRIMARY KEY,
  impact_name TEXT NOT NULL,
  impact_description TEXT
);

CREATE TABLE energy_sources (
  source_id SERIAL PRIMARY KEY,
  source_name TEXT NOT NULL,
  source_type TEXT
);

CREATE TABLE energy_production (
  production_id SERIAL PRIMARY KEY,
  production_name TEXT NOT NULL,
  production_description TEXT,
  source_id INT REFERENCES energy_sources(source_id)
);

CREATE TABLE energy_consumption (
  consumption_id SERIAL PRIMARY KEY,
  consumption_name TEXT NOT NULL,
  consumption_description TEXT,
  source_id INT REFERENCES energy_sources(source_id)
);
