tables.table_schema:
CREATE TABLE biomass_resources (
  id INT PRIMARY KEY,
  location TEXT,
  biomass_type TEXT,
  biomass_quantity DECIMAL
);

CREATE TABLE production_sites (
  id INT PRIMARY KEY,
  site_name TEXT,
  location TEXT,
  capacity DECIMAL
);

CREATE TABLE production_output (
  id INT PRIMARY KEY,
  site_id INT,
  date DATE,
  energy_output DECIMAL
);

CREATE TABLE biomass_resources (
    id UUID PRIMARY KEY,
    region VARCHAR(50),
    biomass_type VARCHAR(50),
    biomass_quantity FLOAT
);

CREATE TABLE biomass_producers (
    id UUID PRIMARY KEY,
    producer_name VARCHAR(100),
    region_id UUID,
    production_capacity INT
);

CREATE TABLE biomass_consumers (
    id UUID PRIMARY KEY,
    consumer_name VARCHAR(100),
    region_id UUID,
    demand INT
);
