tables.table_schema:
CREATE TABLE biofuel_production_systems (
    id INT PRIMARY KEY,
    system_name VARCHAR(255),
    production_capacity DECIMAL,
    production_location VARCHAR(255)
);

CREATE TABLE production_metrics (
    metric_id INT PRIMARY KEY,
    system_id INT,
    production_date DATE,
    efficiency DECIMAL
);

CREATE TABLE Bioenergy_Plants (
  plant_id INT PRIMARY KEY,
  plant_name VARCHAR(50) NOT NULL,
  production_capacity DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Bioenergy_Resources (
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(50) NOT NULL,
  resource_type VARCHAR(20) NOT NULL
);

CREATE TABLE bioenergy_production_data (
    production_id SERIAL PRIMARY KEY,
    production_date DATE,
    biofuel_type VARCHAR(50),
    production_quantity INT
);

CREATE TABLE process_log (
    log_id SERIAL PRIMARY KEY,
    production_id INT,
    log_timestamp TIMESTAMP,
    log_message TEXT
);

CREATE TABLE biofuel_consumption (
    biofuel_id SERIAL PRIMARY KEY,
    vehicle_type VARCHAR(50),
    consumption_amount DECIMAL(8,2),
    consumption_date DATE
);

CREATE TABLE production_facility (
    facility_id SERIAL PRIMARY KEY,
    facility_name VARCHAR(100),
    facility_location VARCHAR(100),
    facility_capacity DECIMAL(10,2)
);
